const loadTemplate=(i,t)=>`\n            <div id="${t}" class="article-big">\n            <div class="article-big__container">\n              <div class="article-big__image">\n                <img src="${i.image}" alt="Человеческий мозг картинка" />\n              </div>\n              <div class="article-big__content">\n                <p class="article-big__date">${i.date}</p>\n                <p id="${i.id}" class="article-big__title">${i.title}</p>\n                <p id="${i.id}" class="article-big__text">${i.text}</p>\n              </div>\n            </div>\n          </div>\n            `;