const loadTemplateM=(i,d)=>{let e="";for(let l=0;l<d;l++)e+=` <div class="article-middle">\n            <div class="article-middle__container">\n              <div class="article-middle__image">\n                    <img src="${i[l].image}" alt="мозг" />\n                </div>\n                <div class="article-middle__content">\n                    <p class="article-middle__date">${i[l].date}</p>\n                    <p id="${i[l].id}" class="article-middle__title">${i[l].title}</p>\n                    <p id="${i[l].id}" class="article-middle__text">${i[l].text}</p>\n                </div>\n                </div>\n            </div>`;return e};